mapper.select:
select
  id, category_id, product_name, product_description, dateline_create, dateline_updated, is_recommend, sort_id
from newxton_companywebsite.nxt_product


        and id = #{id}


        and category_id = #{categoryId}


        and product_name = #{productName}


        and product_description = #{productDescription}


        and dateline_create = #{datelineCreate}


        and dateline_updated = #{datelineUpdated}


        and is_recommend = #{isRecommend}


        sort_id = #{sortId},

select
count(*)
from newxton_companywebsite.nxt_product


        and id = #{id}


        and category_id = #{categoryId}


        and product_name = #{productName}


        and product_description = #{productDescription}


        and dateline_create = #{datelineCreate}


        and dateline_updated = #{datelineUpdated}


