mapper.select:
select
  id, setting_key, setting_value, setting_name, dateline_updated
from newxton_companywebsite.nxt_setting


        and id = #{id}


        and setting_key = #{settingKey}


        and setting_value = #{settingValue}


        and setting_name = #{settingName}


        and dateline_updated = #{datelineUpdated}
